// <⚠️ DONT DELETE THIS ⚠️>
//import "./styles.css";
// <⚠️ /DONT DELETE THIS ⚠️>

const form = document.querySelector("#todoList");
const input = document.querySelector("#todoInput");
let todos = [];

const TODOS_LS = "TODOS";
const STATUS_PENDING = "PENDING";
const STATUS_FINISH = "FINISH";

function saveTodos(todos) {
	localStorage.setItem(TODOS_LS, JSON.stringify(todos));
}

function loadTodos() {
	const loadTodos = localStorage.getItem(TODOS_LS);
	if (!loadTodos) return;
	const parsedTodos = JSON.parse(loadTodos);
	parsedTodos.forEach(function (todo) {
		paintTodo(todo.text, todo.status);
	});
}

function handleSubmit(event) {
	event.preventDefault();
	paintTodo(input.value);
	input.value = "";
}

function paintTodo(text, status = STATUS_PENDING) {
	const ul = document.querySelector(`#pending_todos`);
	const li = document.createElement("li");
	const span = document.createElement("span");
	const deleteBtn = document.createElement("button");
	const changeStatusBtn = document.createElement("button");

	deleteBtn.innerText = "❌";
	deleteBtn.addEventListener("click", deleteTodo);

	changeStatusBtn.addEventListener("click", toggleTodo);

	const newId = Date.now();
	li.id = newId;
	span.innerText = text;
	li.appendChild(span);
	li.appendChild(deleteBtn);
	li.appendChild(changeStatusBtn);
	//set pending tasks
	if (status === STATUS_PENDING) {
		const ul = document.querySelector(`#pending_todos`);
		ul.appendChild(li);
		changeStatusBtn.innerText = "✔";
	}
	//set finish tasks
	else {
		const ul = document.querySelector(`#finish_todos`);
		ul.appendChild(li);
		changeStatusBtn.innerText = "↩";
	}

	const todoObj = {
		id: newId,
		text: text,
		status: status,
	};
	todos.push(todoObj);
	saveTodos(todos);
}

function deleteTodo(event) {
	const li = event.target.parentNode;
	const currentId = li.id;
	li.remove();
	todos = todos.filter(function (todo) {
		return todo.id !== parseInt(currentId);
	});
}

function toggleTodo(event) {
	const li = event.target.parentNode;
	const currentId = li.id;

	//find task using id. it returns only one length of array.
	const findTodo = todos.filter(function (todo) {
		return todo.id === parseInt(currentId);
	});
	const text = findTodo[0].text;
	//toggle status between PENDING and FINISH
	const toggledStatus = findTodo[0].status === STATUS_PENDING ? STATUS_FINISH : STATUS_PENDING;

	//delete tasks already exists in advance
	deleteTodo(event);
	paintTodo(text, toggledStatus);
}

function init() {
	form.addEventListener("submit", handleSubmit);
	loadTodos();
}

init();
